import { 
  type User, 
  type InsertUser, 
  type LoginData,
  type SignupData,
  type ProfileUpdate,
  type UserSession,
  type Quote, 
  type InsertQuote,
  type UserProject,
  type InsertProject,
  type ActivityLog,
  type EmailVerification,
  type AvatarUpload,
  type PreferencesUpdate,
  type ChangePassword,
  type AccountDeletion,
  type Payment,
  type InsertPayment,
  type Subscription,
  type InsertSubscription,
  type Invoice,
  type InsertInvoice,
  type EmailVerificationToken,
  type InsertEmailVerificationToken,
  type PackageViewTracking,
  type InsertPackageViewTracking,
  users, 
  userSessions, 
  quotes, 
  userProjects, 
