      }).from(users).where(eq(users.email, email)).limit(1);

      // Fill in optional fields with defaults for the type
      if (result[0]) {
        return {
          ...result[0],
          jobTitle: null,
          industry: null,
          companySize: null,
          website: null,
          address: null,
          businessGoals: null,
