@tailwind base;
@tailwind components;
@tailwind utilities;

/* Hide scrollbars globally while maintaining functionality */
* {
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* Internet Explorer 10+ */
}

*::-webkit-scrollbar {
  display: none; /* WebKit browsers (Chrome, Safari, Edge) */
}

html, body {
  overflow-x: hidden; /* Prevent horizontal scroll */
  width: 100%;
  max-width: 100vw;
}

/* Ensure all containers respect viewport width */
.container, .max-w-7xl, .max-w-6xl, .max-w-5xl, .max-w-4xl {
  max-width: calc(100vw - 2rem);
  margin-left: auto;
  margin-right: auto;
  padding-left: 1rem;
  padding-right: 1rem;
}

@media (min-width: 640px) {
  .container, .max-w-7xl, .max-w-6xl, .max-w-5xl, .max-w-4xl {
    padding-left: 1.5rem;
    padding-right: 1.5rem;
  }
}

@media (min-width: 1024px) {
  .container {
    max-width: 1024px;
  }
  .max-w-7xl {
    max-width: 1280px;
  }
  .max-w-6xl {
    max-width: 1152px;
  }
  .max-w-5xl {
    max-width: 1024px;
  }
  .max-w-4xl {
    max-width: 896px;
  }
}

/* Mobile-first responsive text scaling */
.responsive-text {
  font-size: clamp(0.875rem, 2.5vw, 1rem);
  line-height: 1.5;
}

.responsive-heading {
  font-size: clamp(1.5rem, 5vw, 3rem);
  line-height: 1.2;
}

.responsive-subheading {
  font-size: clamp(1.125rem, 3.5vw, 1.5rem);
  line-height: 1.3;
}

/* Spring-like button animations */
@keyframes spring-bounce {
  0% { transform: scale(1); }
  20% { transform: scale(0.95); }
  40% { transform: scale(1.02); }
  60% { transform: scale(0.98); }
  80% { transform: scale(1.01); }
  100% { transform: scale(1); }
}

/* Glow pulse animation */
@keyframes glow-pulse {
  0%, 100% { box-shadow: 0 0 20px rgba(132, 204, 22, 0.5); }
  50% { box-shadow: 0 0 30px rgba(132, 204, 22, 0.8); }
}

/* Gradient text animation */
@keyframes gradient-text {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

/* Shimmer effect */
@keyframes shimmer {
  0% { background-position: -1000px 0; }
  100% { background-position: 1000px 0; }
}

/* Floating animation */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

/* Pulsing background */
@keyframes pulse-bg {
  0%, 100% { opacity: 0.1; }
  50% { opacity: 0.2; }
}

/* Spring button class */
.spring-button {
  transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.spring-button:active {
  animation: spring-bounce 0.4s ease;
}

/* Glow button effect */
.glow-button {
  position: relative;
  overflow: hidden;
}

.glow-button::before {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: linear-gradient(45deg, transparent, rgba(255,255,255,0.3), transparent);
  transform: rotate(45deg);
  animation: shimmer 2s infinite;
}

/* Gradient text */
.gradient-text {
  background: linear-gradient(135deg, #3b82f6, #06b6d4, #22c55e);
  background-size: 200% 200%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient-text 3s ease infinite;
}

/* Bento grid hover effects */
.bento-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.bento-card:hover {
  transform: translateY(-8px) scale(1.02);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
}

/* Glassmorphism effect */
.glass-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Loading skeleton shimmer */
.skeleton {
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}

/* Smooth fade in animation */
.fade-in-up {
  animation: fadeInUp 0.6s ease forwards;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
