import { useEffect, useState } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { CheckCircle, ArrowRight, Calendar, FileText, Star, Trophy, Sparkles, Rocket } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

// Confetti component for celebration animation
const Confetti = () => {
  const confettiColors = ['#22c55e', '#06b6d4', '#3b82f6', '#a855f7', '#f59e0b'];

  return (
    <div className="fixed inset-0 pointer-events-none z-50 overflow-hidden">
      {[...Array(50)].map((_, i) => (
        <motion.div
          key={i}
          className="absolute w-2 h-2 rounded-full"
          style={{
            backgroundColor: confettiColors[Math.floor(Math.random() * confettiColors.length)],
            left: `${Math.random() * 100}%`,
          }}
          initial={{
            y: -100,
            x: Math.random() * 20 - 10,
            rotate: 0,
            scale: 0
          }}
          animate={{
            y: window.innerHeight + 100,
            rotate: 360,
            scale: [0, 1, 1, 0]
          }}
          transition={{
            duration: 3,
            delay: Math.random() * 2,
            ease: "easeOut"
          }}
        />
      ))}
    </div>
  );
};

export default function PaymentSuccess() {
  const [, setLocation] = useLocation();
  const [showConfetti, setShowConfetti] = useState(true);

  useEffect(() => {
    // Show confetti animation and then hide it
    const timer = setTimeout(() => {
      setShowConfetti(false);
    }, 5000);

    // You could track payment success analytics here
    console.log('Payment completed successfully');

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-teal-50 to-blue-50 pt-20 overflow-hidden relative">
      {/* Confetti Animation */}
      <AnimatePresence>
        {showConfetti && <Confetti />}
      </AnimatePresence>

