          <Button 
            onClick={() => {
              onOpenChange(false);
              window.location.href = '/recommendations';
            }}
            className="w-full bg-teal-primary text-white hover:bg-teal-600"
          >
            Get Personalized Package Recommendations
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

