import React from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import FileManager from '@/components/ui/file-manager';
import { 
  Cloud, 
  HardDrive, 
  Upload, 
  Download,
  FileText,
  Image,
  Video,
  Music,
  Archive,
  AlertCircle
} from 'lucide-react';

interface FileDashboardData {
  summary: {
    totalFiles: number;
    totalSize: number;
    totalSizeFormatted: string;
    filesByType: {
      image: number;
      video: number;
      document: number;
      audio: number;
      archive: number;
      other: number;
    };
  };
  files: {
    filename: string;
    mimetype: string;
    size: number;
    cloudinary_url: string;
    cloudinary_public_id: string;
    upload_date: string;
    quoteId: number;
    quoteEmail: string;
    category: 'image' | 'video' | 'document' | 'audio' | 'archive' | 'other';
    optimized_url: string;
  }[];
}

export default function AdminFileManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch file dashboard data
  const { data, isLoading, error } = useQuery<FileDashboardData>({
    queryKey: ['/api/admin/files/dashboard'],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Delete files mutation
  const deleteFilesMutation = useMutation({
    mutationFn: async (publicIds: string[]) => {
      const response = await fetch('/api/files/delete', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ publicIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete files');
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: 'Files deleted',
        description: data.message,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/files/dashboard'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error deleting files',
        description: error.message || 'Failed to delete files',
        variant: 'destructive',
      });
    },
  });

  const handleRefresh = () => {
    queryClient.invalidateQueries({ queryKey: ['/api/admin/files/dashboard'] });
    toast({
      title: 'Refreshing',
      description: 'File data is being refreshed...',
    });
  };

  const handleDelete = (publicIds: string[]) => {
    if (confirm(`Are you sure you want to delete ${publicIds.length} file(s)? This action cannot be undone.`)) {
      deleteFilesMutation.mutate(publicIds);
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-gray-200 rounded w-1/3"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-gray-200 rounded"></div>
            ))}
          </div>
          <div className="h-96 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="p-8 text-center">
            <AlertCircle className="h-12 w-12 mx-auto text-red-500 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Error loading file data</h3>
            <p className="text-gray-500 mb-4">
              Failed to load file management dashboard. Please try again.
            </p>
            <Button onClick={handleRefresh}>Retry</Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const summary = data?.summary || {
    totalFiles: 0,
    totalSize: 0,
    totalSizeFormatted: '0 MB',
    filesByType: { image: 0, video: 0, document: 0, audio: 0, archive: 0, other: 0 }
  };

  const files = data?.files || [];

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">File Management</h1>
          <p className="text-gray-500 mt-1">
            Manage all uploaded files from quotes and other forms
          </p>
        </div>

        <div className="flex items-center space-x-2">
          <div className="flex items-center space-x-1 text-green-600">
            <Cloud className="h-4 w-4" />
            <span className="text-sm font-medium">Cloudinary Connected</span>
          </div>
        </div>
      </div>

      {/* Storage Overview */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <HardDrive className="h-5 w-5 mr-2" />
            Storage Overview
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4">
            <div className="text-center">
              <FileText className="h-8 w-8 mx-auto text-green-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.document}</div>
              <div className="text-sm text-gray-500">Documents</div>
            </div>

            <div className="text-center">
              <Image className="h-8 w-8 mx-auto text-blue-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.image}</div>
              <div className="text-sm text-gray-500">Images</div>
            </div>

            <div className="text-center">
              <Video className="h-8 w-8 mx-auto text-purple-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.video}</div>
              <div className="text-sm text-gray-500">Videos</div>
            </div>

            <div className="text-center">
              <Music className="h-8 w-8 mx-auto text-yellow-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.audio}</div>
              <div className="text-sm text-gray-500">Audio</div>
            </div>

            <div className="text-center">
              <Archive className="h-8 w-8 mx-auto text-orange-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.archive}</div>
              <div className="text-sm text-gray-500">Archives</div>
            </div>

            <div className="text-center">
              <FileText className="h-8 w-8 mx-auto text-gray-500 mb-2" />
              <div className="text-2xl font-bold">{summary.filesByType.other}</div>
              <div className="text-sm text-gray-500">Other</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* File Manager */}
      <Tabs defaultValue="all" className="space-y-4">
        <TabsList>
          <TabsTrigger value="all">All Files ({summary.totalFiles})</TabsTrigger>
          <TabsTrigger value="recent">Recent Uploads</TabsTrigger>
          <TabsTrigger value="large">Large Files</TabsTrigger>
        </TabsList>

        <TabsContent value="all">
          <FileManager
            files={files}
            summary={summary}
            onRefresh={handleRefresh}
            onDelete={handleDelete}
          />
        </TabsContent>

        <TabsContent value="recent">
          <FileManager
            files={files.slice(0, 20)} // Show last 20 files
            onRefresh={handleRefresh}
            onDelete={handleDelete}
          />
        </TabsContent>

        <TabsContent value="large">
          <FileManager
            files={files.filter(file => file.size > 1024 * 1024)} // Files larger than 1MB
            onRefresh={handleRefresh}
            onDelete={handleDelete}
          />
        </TabsContent>
      </Tabs>

      {/* Cloudinary Usage Info */}
      <Card>
        <CardHeader>
          <CardTitle className="text-sm text-gray-700">Cloudinary Free Tier Usage</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-gray-600">
            <div className="flex justify-between items-center mb-2">
              <span>Storage Used:</span>
              <span className="font-medium">{summary.totalSizeFormatted} / 25 GB</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-blue-600 h-2 rounded-full" 
                style={{ 
                  width: `${Math.min((summary.totalSize / (25 * 1024 * 1024 * 1024)) * 100, 100)}%` 
                }}
              ></div>
            </div>
            <p className="text-xs text-gray-500 mt-2">
              Files are automatically optimized and delivered via Cloudinary's CDN for fast loading.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/verify-email.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation, useRoute } from 'wouter';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { CheckCircle, Mail, AlertCircle, Loader2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

export default function VerifyEmail() {
  const [, setLocation] = useLocation();
  const [, params] = useRoute('/verify-email');
  const { toast } = useToast();
  const [verificationStatus, setVerificationStatus] = useState<'loading' | 'success' | 'error' | 'expired'>('loading');
  const [message, setMessage] = useState('');
  const [isResending, setIsResending] = useState(false);

  useEffect(() => {
    const verifyEmail = async () => {
      const urlParams = new URLSearchParams(window.location.search);
      const token = urlParams.get('token');

      if (!token) {
        setVerificationStatus('error');
        setMessage('No verification token provided');
        return;
      }

      try {
        const response = await fetch(`/api/auth/verify-email?token=${token}`);
        const data = await response.json();

        if (response.ok) {
          setVerificationStatus('success');
          setMessage(data.message);
          toast({
            title: "Email Verified!",
            description: "Your email has been verified successfully. You can now make purchases.",
          });

          // Redirect to login after a short delay
          setTimeout(() => {
            setLocation('/login');
          }, 3000);
        } else {
          setVerificationStatus('error');
          setMessage(data.message || 'Verification failed');
        }
      } catch (error) {
        console.error('Verification error:', error);
        setVerificationStatus('error');
        setMessage('An error occurred during verification');
      }
    };

    verifyEmail();
  }, []);

  const handleResendVerification = async () => {
    setIsResending(true);
    try {
      const response = await fetch('/api/auth/resend-verification', {
        method: 'POST',
        credentials: 'include'
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Verification Email Sent",
          description: "Please check your email for the new verification link.",
        });
      } else {
        toast({
          title: "Failed to Send Email",
          description: data.message || "Could not send verification email. Please try again.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "An error occurred. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsResending(false);
    }
  };

  const renderContent = () => {
    switch (verificationStatus) {
      case 'loading':
        return (
          <div className="text-center py-8">
            <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-blue-600" />
            <h2 className="text-xl font-semibold mb-2">Verifying Your Email</h2>
            <p className="text-gray-600">Please wait while we verify your email address...</p>
          </div>
        );

      case 'success':
        return (
          <div className="text-center py-8">
            <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-green-700 mb-2">Email Verified!</h2>
            <p className="text-gray-600 mb-4">{message}</p>
            <Alert className="mb-6">
              <CheckCircle className="h-4 w-4" />
              <AlertDescription>
                Your account is now fully activated. You can make purchases and access all features.
              </AlertDescription>
            </Alert>
            <p className="text-sm text-gray-500">Redirecting you to login in a few seconds...</p>
          </div>
        );

      case 'error':
        return (
          <div className="text-center py-8">
            <AlertCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-red-700 mb-2">Verification Failed</h2>
            <p className="text-gray-600 mb-6">{message}</p>

            <Alert className="mb-6" variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                The verification link may have expired or been used already.
              </AlertDescription>
            </Alert>

            <div className="space-y-4">
              <Button 
                onClick={handleResendVerification}
                disabled={isResending}
                className="w-full"
              >
                {isResending ? (
                  <>
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                    Sending...
                  </>
                ) : (
                  <>
                    <Mail className="h-4 w-4 mr-2" />
                    Resend Verification Email
                  </>
                )}
              </Button>

              <Button 
                variant="outline" 
                onClick={() => setLocation('/login')}
                className="w-full"
              >
                Go to Login
              </Button>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full">
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-bold text-gray-900">
              Email Verification
            </CardTitle>
          </CardHeader>
          <CardContent>
            {renderContent()}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/ui/file-manager.ts.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Separator } from '@/components/ui/separator';
import { 
  FileText, 
  Image, 
  Video, 
  Music, 
  Archive, 
  Download, 
  ExternalLink, 
  Trash2,
  Upload,
  Eye,
  Search,
  Filter,
  Grid,
  List
} from 'lucide-react';

interface FileData {
  filename: string;
  mimetype: string;
  size: number;
  cloudinary_url: string;
  cloudinary_public_id: string;
  upload_date: string;
  quoteId?: number;
  quoteEmail?: string;
  category: 'image' | 'video' | 'document' | 'audio' | 'archive' | 'other';
  optimized_url?: string;
}

interface FileManagerProps {
  files: FileData[];
  summary?: {
    totalFiles: number;
    totalSize: number;
    totalSizeFormatted: string;
    filesByType: Record<string, number>;
  };
  onRefresh?: () => void;
  onDelete?: (publicIds: string[]) => void;
}

const getFileIcon = (category: string) => {
  switch (category) {
    case 'image': return <Image className="h-5 w-5 text-blue-500" />;
    case 'video': return <Video className="h-5 w-5 text-purple-500" />;
    case 'document': return <FileText className="h-5 w-5 text-green-500" />;
    case 'audio': return <Music className="h-5 w-5 text-yellow-500" />;
    case 'archive': return <Archive className="h-5 w-5 text-orange-500" />;
    default: return <FileText className="h-5 w-5 text-gray-500" />;
  }
};

const formatFileSize = (bytes: number) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

export default function FileManager({ files, summary, onRefresh, onDelete }: FileManagerProps) {
  const [selectedFiles, setSelectedFiles] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [previewFile, setPreviewFile] = useState<FileData | null>(null);

  // Filter files based on search and category
  const filteredFiles = files.filter(file => {
    const matchesSearch = file.filename.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         file.quoteEmail?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = filterCategory === 'all' || file.category === filterCategory;
    return matchesSearch && matchesCategory;
  });

  const handleSelectFile = (publicId: string) => {
    setSelectedFiles(prev => 
      prev.includes(publicId) 
        ? prev.filter(id => id !== publicId)
        : [...prev, publicId]
    );
  };

  const handleDeleteSelected = () => {
    if (selectedFiles.length > 0 && onDelete) {
      onDelete(selectedFiles);
      setSelectedFiles([]);
    }
  };

  const FilePreview = ({ file }: { file: FileData }) => {
    if (file.category === 'image') {
      return (
        <img 
          src={file.optimized_url || file.cloudinary_url} 
          alt={file.filename}
          className="max-w-full max-h-96 object-contain rounded-lg"
        />
      );
    } else if (file.category === 'video') {
      return (
        <video 
          src={file.cloudinary_url} 
          controls 
          className="max-w-full max-h-96 rounded-lg"
        />
      );
    } else {
      return (
        <div className="flex flex-col items-center justify-center p-8 border-2 border-dashed border-gray-300 rounded-lg">
          {getFileIcon(file.category)}
          <p className="mt-2 text-sm text-gray-600">Preview not available</p>
          <p className="text-xs text-gray-500">{file.filename}</p>
        </div>
      );
    }
  };

  return (
    <div className="space-y-6">
      {/* Summary Cards */}
      {summary && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Total Files</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{summary.totalFiles}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Total Size</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{summary.totalSizeFormatted}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Images</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600">{summary.filesByType.image || 0}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Documents</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{summary.filesByType.document || 0}</div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Controls */}
      <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
        <div className="flex flex-col sm:flex-row gap-2 flex-1">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Search files..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 w-full sm:w-64"
            />
          </div>

          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-md text-sm"
          >
            <option value="all">All Categories</option>
            <option value="image">Images</option>
            <option value="video">Videos</option>
            <option value="document">Documents</option>
            <option value="audio">Audio</option>
            <option value="archive">Archives</option>
          </select>
        </div>

        <div className="flex gap-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setViewMode(viewMode === 'grid' ? 'list' : 'grid')}
          >
            {viewMode === 'grid' ? <List className="h-4 w-4" /> : <Grid className="h-4 w-4" />}
          </Button>

          {selectedFiles.length > 0 && (
            <Button
              variant="destructive"
              size="sm"
              onClick={handleDeleteSelected}
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Delete ({selectedFiles.length})
            </Button>
          )}

          {onRefresh && (
            <Button variant="outline" size="sm" onClick={onRefresh}>
              Refresh
            </Button>
          )}
        </div>
      </div>

      {/* File List */}
      {viewMode === 'grid' ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {filteredFiles.map((file) => (
            <Card key={file.cloudinary_public_id} className="group hover:shadow-md transition-shadow">
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-2">
                  <input
                    type="checkbox"
                    checked={selectedFiles.includes(file.cloudinary_public_id)}
                    onChange={() => handleSelectFile(file.cloudinary_public_id)}
                    className="rounded"
                  />
                  <Badge variant="secondary" className="text-xs">
                    {file.category}
                  </Badge>
                </div>

                <div className="flex items-center mb-2">
                  {getFileIcon(file.category)}
                  <span className="ml-2 text-sm font-medium truncate flex-1">
                    {file.filename}
                  </span>
                </div>

                <div className="text-xs text-gray-500 space-y-1">
                  <div>Size: {formatFileSize(file.size)}</div>
                  <div>Uploaded: {new Date(file.upload_date).toLocaleDateString()}</div>
                  {file.quoteEmail && (
                    <div>Quote: {file.quoteEmail}</div>
                  )}
                </div>

                <div className="flex justify-between mt-3">
                  <Dialog>
                    <DialogTrigger asChild>
                      <Button variant="outline" size="sm" onClick={() => setPreviewFile(file)}>
                        <Eye className="h-3 w-3 mr-1" />
                        Preview
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="max-w-4xl">
                      <DialogHeader>
                        <DialogTitle>{file.filename}</DialogTitle>
                      </DialogHeader>
                      <FilePreview file={file} />
                    </DialogContent>
                  </Dialog>

                  <Button variant="outline" size="sm" asChild>
                    <a href={file.cloudinary_url} target="_blank" rel="noopener noreferrer">
                      <ExternalLink className="h-3 w-3" />
                    </a>
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <div className="space-y-2">
          {filteredFiles.map((file) => (
            <Card key={file.cloudinary_public_id}>
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4 flex-1">
                    <input
                      type="checkbox"
                      checked={selectedFiles.includes(file.cloudinary_public_id)}
                      onChange={() => handleSelectFile(file.cloudinary_public_id)}
                      className="rounded"
                    />
                    {getFileIcon(file.category)}
                    <div className="flex-1 min-w-0">
                      <div className="font-medium truncate">{file.filename}</div>
                      <div className="text-sm text-gray-500">
                        {formatFileSize(file.size)} ‚Ä¢ {new Date(file.upload_date).toLocaleDateString()}
                        {file.quoteEmail && ` ‚Ä¢ Quote: ${file.quoteEmail}`}
                      </div>
                    </div>
                    <Badge variant="secondary">{file.category}</Badge>
                  </div>

                  <div className="flex space-x-2">
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button variant="outline" size="sm" onClick={() => setPreviewFile(file)}>
                          <Eye className="h-4 w-4" />
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-4xl">
                        <DialogHeader>
                          <DialogTitle>{file.filename}</DialogTitle>
                        </DialogHeader>
                        <FilePreview file={file} />
                      </DialogContent>
                    </Dialog>

                    <Button variant="outline" size="sm" asChild>
                      <a href={file.cloudinary_url} target="_blank" rel="noopener noreferrer">
                        <ExternalLink className="h-4 w-4" />
                      </a>
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {filteredFiles.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <FileText className="h-12 w-12 mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No files found</h3>
            <p className="text-gray-500">
              {searchTerm || filterCategory !== 'all' 
                ? 'Try adjusting your search or filter criteria.'
                : 'Files will appear here when users upload attachments to quotes.'
              }
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

server/audio-upload-routes.ts.content:
import express from 'express';
import multer from 'multer';
import { uploadToCloudinary } from './cloudinary-config';

const router = express.Router();

// Configure multer for audio uploads
const audioUpload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 50 * 1024 * 1024, // 50MB limit
  },
  fileFilter: (req, file, cb) => {
    // Accept audio files and webm (for recordings)
    const allowedMimes = [
      'audio/mpeg', 'audio/wav', 'audio/ogg', 'audio/mp3', 'audio/mp4', 'audio/m4a',
      'audio/webm', 'audio/x-wav', 'audio/aac', 'video/webm' // webm can contain audio
    ];

    if (allowedMimes.includes(file.mimetype) || file.originalname.endsWith('.webm')) {
      cb(null, true);
    } else {
      cb(new Error(`Audio file type ${file.mimetype} not allowed`));
    }
  },
});

// Upload audio recording to Cloudinary
router.post('/upload-audio', audioUpload.single('audio'), async (req, res) => {
  try {
    if (!req.file) {
      return res.status(400).json({ error: 'No audio file provided' });
    }

    const { originalname, buffer, mimetype, size } = req.file;
    const { recordingName, quoteId } = req.body;

    // Generate a unique filename for the recording
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const filename = recordingName || `voice-recording-${timestamp}`;
    const folder = quoteId ? `quotes/${quoteId}/audio` : 'audio-recordings';

    // Upload to Cloudinary as raw resource type for audio
    const cloudinaryResult = await uploadToCloudinary(
      buffer,
      `${filename}.${mimetype.split('/')[1] || 'webm'}`,
      folder,
      'raw'
    );

    // Return the upload result
    res.json({
      success: true,
      audio: {
        filename: filename,
        originalName: originalname,
        mimetype: mimetype,
        size: size,
        cloudinary_url: cloudinaryResult.secure_url,
        cloudinary_public_id: cloudinaryResult.public_id,
        upload_date: new Date().toISOString(),
        format: cloudinaryResult.format,
        bytes: cloudinaryResult.bytes
      }
    });

  } catch (error: any) {
    console.error('Audio upload error:', error);
    res.status(500).json({ 
      error: 'Failed to upload audio recording',
      message: error?.message || 'Unknown error occurred'
    });
  }
});

// Convert blob URL to file for upload
router.post('/upload-recording-blob', async (req, res) => {
  try {
    const { audioBlob, recordingName, quoteId } = req.body;

    if (!audioBlob) {
      return res.status(400).json({ error: 'No audio blob provided' });
    }

    // Convert base64 to buffer
    const base64Data = audioBlob.split(',')[1];
    const buffer = Buffer.from(base64Data, 'base64');

    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const filename = recordingName || `voice-recording-${timestamp}`;

    try {
      // Try to upload to Cloudinary
      const cloudinaryResult = await uploadToCloudinary(
        buffer,
        filename,
        'audio-recordings', // Use folder again
        'raw'
      );

      res.json({
        success: true,
        audio: {
          filename: filename,
          mimetype: 'audio/webm',
          size: buffer.length,
          cloudinary_url: cloudinaryResult.secure_url,
          cloudinary_public_id: cloudinaryResult.public_id,
          upload_date: new Date().toISOString(),
          format: cloudinaryResult.format,
          bytes: cloudinaryResult.bytes,
          storage: 'cloudinary'
        }
      });

    } catch (cloudinaryError: any) {
      console.warn('Cloudinary upload failed, providing local fallback:', cloudinaryError.message);

      // Return success with local storage info - audio will remain in browser blob storage
      res.json({
        success: true,
        audio: {
          filename: filename,
          mimetype: 'audio/webm',
          size: buffer.length,
          local_blob: true, // Indicates this is stored locally in the browser
          upload_date: new Date().toISOString(),
          storage: 'local',
          note: 'Audio stored locally - will be included when you submit your quote'
        }
      });
    }

  } catch (error: any) {
    console.error('Blob upload error:', error);

    // Even if there's an outer error, provide fallback response
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const filename = req.body.recordingName || `voice-recording-${timestamp}`;

    res.json({
      success: true,
      audio: {
        filename: filename,
        mimetype: 'audio/webm',
        size: req.body.audioBlob ? Buffer.from(req.body.audioBlob.split(',')[1], 'base64').length : 0,
        local_blob: true,
        upload_date: new Date().toISOString(),
        storage: 'local',
        note: 'Audio stored locally - will be included when you submit your quote'
      }
    });
  }
});

// Get audio file from Cloudinary
router.get('/audio/:publicId', async (req, res) => {
  try {
    const { publicId } = req.params;

    // Cloudinary automatically serves files via their CDN
    // We can construct the URL or redirect to it
    const audioUrl = `https://res.cloudinary.com/${process.env.CLOUDINARY_CLOUD_NAME}/raw/upload/${publicId}`;

    res.json({
      success: true,
      audio_url: audioUrl,
      public_id: publicId
    });

  } catch (error: any) {
    console.error('Audio retrieval error:', error);
    res.status(500).json({ 
      error: 'Failed to retrieve audio file',
      message: error?.message || 'Unknown error occurred'
    });
  }
});

export default router;

